// dynamic datas
const state = reactive({
  errors: [],
  loading: false

});
const user = reactive({
  name: "",
  phone: "",
  message: "",
});

const tooltip = ref(null);
const uzbekistan = ref(null);
const listeners = [];

const isDisabled = computed(() => {
  return state.loading || validate().length > 0;
});

// methods
function validate() {
  const errors = [];
  if (!user.phone || user.phone.length < 19) errors.push({ path: "phone", message: user.phone.length < 19 && user.phone.length > 0 ? translations.value['contact.phone_full'] : translations.value['contact.can_not_blank'] });
  if (!user.name) errors.push({ path: "name", message: translations.value['contact.can_not_blank'] });
  if (!user.message) errors.push({ path: "message", message: translations.value['contact.can_not_blank'] });

  return errors;
};

async function submitMessage() {
  state.loading = true;
  try {
    let res = await useServices().postServices("/application/create", {
      ...user
    });
    if (res.status === 201) {
      state.errors = [];
      user.name = "";
      user.phone = "";
      user.message = "";
    } else {
      state.errors = [{ path: "form", message: "Something went wrong" }];
    }
  } catch {
    state.errors = [{ path: "form", message: "Something went wrong" }];
  } finally {
    state.loading = false;
  }
}

onMounted(() => {
  tooltip.value = document.getElementById('tooltip');
  uzbekistan.value = document.getElementById('uzbekistan');
  uzbekistan.value.childNodes.forEach(node => {
    if (node.nodeType === Node.ELEMENT_NODE) {
      const mouseEnterListener = (event) => {
        tooltip.value.style.display = 'block';
        const title = event.target.getAttribute('title');
        const id = event.target.getAttribute('id');
        tooltip.value.innerHTML = title + (id === 'UZ-AR' ? `<span style='color: #97A9A4'> dengizi</span>` : id === 'UZ-QR' ? `<span style='color: #97A9A4'> Respublikasi</span>` : id === 'UZ-TK' ? `<span style='color: #97A9A4'> shahri</span>` : `<span style='color: #97A9A4'> viloyati</span>`);
      };
      const mouseMoveListener = (event) => {
        tooltip.value.style.left = event.offsetX - tooltip.value.clientWidth / 2 + 'px';
        tooltip.value.style.top = event.offsetY - 60 + 'px';
      };
      const mouseLeaveListener = () => {
        tooltip.value.style.display = 'none';
      };

      node.addEventListener('mouseenter', mouseEnterListener);
      node.addEventListener('mousemove', mouseMoveListener);
      node.addEventListener('mouseleave', mouseLeaveListener);

      listeners.push({ node, mouseEnterListener, mouseMoveListener, mouseLeaveListener });
    }
  });
});

onUnmounted(() => {
  listeners.forEach(({ node, mouseEnterListener, mouseMoveListener, mouseLeaveListener }) => {
    node.removeEventListener('mouseenter', mouseEnterListener);
    node.removeEventListener('mousemove', mouseMoveListener);
    node.removeEventListener('mouseleave', mouseLeaveListener);
  });
});
